package org.dashj.merk

import org.dashj.platform.dpp.identifier.Identifier
import org.dashj.platform.dpp.toHexString
import org.dashj.platform.dpp.util.Cbor
import org.dashj.platform.dpp.util.HashUtils
import org.junit.jupiter.api.Assertions.assertTrue
import org.junit.jupiter.api.Assertions.fail
import org.junit.jupiter.api.Test

class MerkVerifyProofTest {

    @OptIn(ExperimentalUnsignedTypes::class)
    @Test
    fun verifyProofTest() {
        MerkVerifyProof.init()

        // rootTreeProof=[1, 0, 0, 0, 2, -80, 84, 121, 67, -44, 2, -80, -21, 119, 114, -121, 92, 38, -116, 21, -56, -7, 98, -62, 4, -39, -27, 79, -96, 96, -107, 1, -107, 58, 63, 109, 56, -29, -57, 23, -48, -118, -48, 36, -12, 1, 2]
        val storeTreeProof = HashUtils.fromHex(
            ""
        )

        val bytes = byteArrayOf(
            22, 5, 80, 35,
            180.toByte(), 73, 66, 146.toByte(), 123, 126, 33, 208.toByte(), 18,
            200.toByte(), 137.toByte(), 167.toByte(), 217.toByte(), 14, 200.toByte(), 155.toByte()
        )
        println("Kotlin Parameters that are passed to Rust:")
        println("bytes: ${storeTreeProof.toHexString()}")
        println("key:   ${Identifier.from("4KJF8PDRAw1rFrFTD95fE3QeutG47TwsJWjBs3wUhNWK").toBuffer().toHexString()}")
        println("hash:  ${bytes.toHexString()}")
        val result = MerkVerifyProof.extractProof(storeTreeProof)
        /*val result = MerkVerifyProof.verifyProof(
            storeTreeProof,
            Identifier.from("AeMJqwVWRDcvWDu8RUwpxsAe6iAtbGxeY6Xw717Bjj4H").toBuffer(),
            bytes
        )*/
        if (result.isNotEmpty()) {
            val firstKey = result.keys.first()
            val firstValue = result.values.first()
            println("key returned: ${firstKey.toByteArray().toHexString()}")
            println("key returned: ${Identifier.from(firstKey.toByteArray())}")
            println("value returned: ${firstValue.toHexString()}")
            println("value extracted: ${Cbor.decode(firstValue)}")
        } else {
            println("verification failed")
            fail<Nothing>("verification failed")
        }

        MerkVerifyProof.decode(storeTreeProof)
    }

    @Test
    fun testRootHash() {
        val storeProof = HashUtils.fromHex("")
        println("Kotlin Store Proof: $storeProof")
        val (hash, map) = MerkVerifyProof.extractProofWithHash(storeProof)
        println("root hash: ${hash.toHexString()}")

        val map2 = hashMapOf<ByteArrayKey, ByteArray>()
        val verified = MerkVerifyProof.verifyProof(storeProof, hash, map2)
        assertTrue(verified)
        println(map2)
    }
}
